output = function (cb) {

  var actor = input.actor || this.getParent();

  // probably should do almost the same as npmlog monitor

  actor.on('inputRequired', function (data) {
    cb({
      error: data
    });
  });

  actor.on('error', function (data) {
    cb({
      error: data
    });
  });

  actor.on('addNode', function (event) {
    cb({
      addNode: event.node
    });
  });

  actor.on('removeNode', function (event) {
    cb({
      removeNode: event.node
    });
  });

  actor.on('addLink', function (link) {
    cb({
      addLink: link
    });
  });

  actor.on('removeLink', function (link) {
    cb({
      removeLink: link
    });
  });

  actor.ioHandler.on('connect', function (link) {
    cb({
      connectLink: link
    });
  });

  actor.ioHandler.on('disconnect', function (link) {
    cb({
      disconnectLink: link
    });
  });

  cb({
    qm: actor.ioHandler.queueManager,
    io: actor.ioHandler
  });

  // not really useful I guess
  /*
  actor.ioHandler.on('data', function (link) {
    cb({
      disconnectLink: link
    });
  });
  */

};

output = function (cb) {
  if (input.in[input.method]) {
    cb({
      out: input.in[input.method].call(input.in, input.args)
    });
  } else {
    cb({
      error: new Error('No such input method ' + input.method)
    });
  }
};

/**
 *
 * Idea is to use :start to trigger output.
 * In which case it just outputs the reference we have
 * In other words make the reference move and become active.
 * It can actually already be done with persist and start and
 * object/create.
 */
on.input.in = function (data) {
};
